package com.iskaypet.controllers;import com.iskaypet.dto.UserDTO;import com.iskaypet.services.UserService;import io.javalin.http.Context;import io.reactivex.rxjava3.core.Observable;import jakarta.inject.Inject;import jakarta.inject.Singleton;import java.util.List;@Singletonpublic class UserController {    @Inject    UserService userService;    public Observable<List<UserDTO>> getUsers(Context context) {        return this.userService.getUsers();    }}