package com.iskaypet.core;import io.javalin.Javalin;import io.javalin.config.JavalinConfig;import io.javalin.http.Context;import io.reactivex.rxjava3.core.Observable;import java.util.function.Consumer;import java.util.function.Function;public record RxJavalin(Javalin javalin) {    public static RxJavalin create(Consumer<JavalinConfig> config) {        return new RxJavalin(Javalin.create(config));    }    public <T> void get(String path, Function<Context, Observable<T>> handler) {        this.javalin.get(path, RxHttpHandler.intercept(handler));    }    public void start(int port) {        this.javalin.start(port);    }}